  `ctime` datetime DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`uid`,`username`,`passwd`)
) ENGINE=InnoDB AUTO_INCREMENT=48 DEFAULT CHARSET=utf8;

